const App = () => {
  const course = "Half Stack application development";
  const part1 = "Fundamentals of React";
  const exercises1 = 10;
  const part2 = "Using props to pass data";
  const exercises2 = 7;
  const part3 = "State of a component";
  const exercises3 = 14;

  return (
    <div>
      <header>{course}</header>
      <content>
        <p>
          {part1} {exercises1}
        </p>
        <p>
          {part3} {exercises2}
        </p>
        <p>
          {part3} {exercises3}
        </p>
      </content>

      <total>Number of exercises {exercises1 + exercises2 + exercises3}</total>
    </div>
  );
};
export default App;
